function ajaxAPI(o, n, t, a) {
    $.ajax({
        url: api_url,
        method: "post",
        data: {
            value: t,
            menu: o,
            url_action: n,
            action: a,
            _token: _token
        },
        success: function(o) {
            return JSON.parse(o);
        },
        error: function(o, n, t) {
            console.log(o.status + " , " + n + ", " + t);
        },
        complete: function(o) {
            getMatrix();
        }
    });
}

$(document).ready(function() {
    $(window).scroll(function() {
        $(window).scrollTop() > notif_top ? $("#notif-area").addClass("fixed") : $("#notif-area").removeClass("fixed");
    });
});